import Foundation

/// Prints a static header for the generated file
struct HeaderPrinter: SwiftCodeConverible {
  let swiftCode = [
    "//",
    "// This is a generated file, do not edit!",
    "// Generated by forked R.swift, see https://github.com/kupibilet-iOS/R.swift",
    "// All credits go to the original repo: https://github.com/mac-cain13/R.swift",
    "//",
  ].joined(separator: "\n")
}
